INSERT INTO `weibo` VALUES ('7497', 'Arber', '转发微博#￥#大约十年前，我有一个极有文彩的同事还当着王老五。他写了一篇对未来充满梦想的文章，结尾处有一句话至今想来都觉得温暖。他想像中的女主角给夜归的他留了一张纸条：＂亲爱的，饭在锅里，我在床上。＂后来我问过周边已婚的朋友们，夫妻间最常见的信息内容是什么。结果，排列第一的是＂不回家吃饭了。＂', 'Sat Jun 22 12:36:42 CST 2013');
